'use client';

import { useEffect, useState } from 'react';
import { useRouter, useParams } from 'next/navigation';
import {
  Container,
  Typography,
  Box,
  Paper,
  Grid,
  Chip,
  Button,
  Divider,
  CircularProgress,
} from '@mui/material';
import { ArrowBack, Download, QrCode2, Edit } from '@mui/icons-material';
import QRCode from 'qrcode';
import { generateCertificatePDF } from '@/lib/utils/pdf-generator';
import apiClient from '@/lib/api-client';

export default function CertificateDetailPage() {
  const router = useRouter();
  const params = useParams();
  const [certificate, setCertificate] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [qrCodeUrl, setQrCodeUrl] = useState('');

  useEffect(() => {
    const fetchCertificate = async () => {
      const id = params.id as string;
      try {
        const response = await apiClient.get(`/certificates/${id}`);
        if (response.data.success) {
          const certData = response.data.data;
          const mappedCert = {
            id: certData.certificateNumber, // Display ID
            _id: certData._id, // Internal ID
            farmName: certData.farmName || 'Unknown Farm',
            status: certData.status,
            issueDate: new Date(certData.validFrom).toLocaleDateString('en-GB'),
            expiryDate: new Date(certData.validUntil).toLocaleDateString('en-GB'),
            // Keep original data for PDF/QR if needed
            ...certData
          };
          setCertificate(mappedCert);

          // Generate QR Code
          try {
            const url = await QRCode.toDataURL(JSON.stringify({
              id: mappedCert.id,
              verifyUrl: `https://gacp.go.th/verify/${mappedCert.id}`
            }));
            setQrCodeUrl(url);
          } catch (err) {
            console.error('QR Gen Error', err);
          }
        }
      } catch (err) {
        console.error('Failed to fetch certificate:', err);
        // Fallback or error handling
      } finally {
        setLoading(false);
      }
    };

    if (params.id) {
      fetchCertificate();
    }
  }, [params.id]);

  const handleDownloadPDF = () => {
    if (certificate) {
      generateCertificatePDF(certificate);
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="50vh">
        <CircularProgress />
      </Box>
    );
  }

  if (!certificate) {
    return (
      <Container maxWidth="md" sx={{ py: 4 }}>
        <Typography variant="h5" color="error" align="center">
          Certificate not found
        </Typography>
        <Box display="flex" justifyContent="center" mt={2}>
          <Button startIcon={<ArrowBack />} onClick={() => router.back()}>
            Back to List
          </Button>
        </Box>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Button
        startIcon={<ArrowBack />}
        onClick={() => router.back()}
        sx={{ mb: 2 }}
      >
        Back to List
      </Button>

      <Paper sx={{ p: 4 }}>
        <Box display="flex" justifyContent="space-between" alignItems="flex-start" mb={3}>
          <Box>
            <Typography variant="h4" gutterBottom>
              {certificate.id}
            </Typography>
            <Chip
              label={certificate.status}
              color={
                certificate.status === 'Active'
                  ? 'success'
                  : certificate.status === 'Expired'
                  ? 'error'
                  : 'default'
              }
            />
          </Box>
          <Box display="flex" gap={1}>
            <Button variant="outlined" startIcon={<Edit />}>
              Edit
            </Button>
            <Button
              variant="contained"
              startIcon={<Download />}
              onClick={handleDownloadPDF}
            >
              PDF
            </Button>
          </Box>
        </Box>

        <Divider sx={{ my: 3 }} />

        <Grid container spacing={3}>
          <Grid item xs={12} md={6}>
            <Typography variant="subtitle2" color="text.secondary">
              Farm Name
            </Typography>
            <Typography variant="h6">{certificate.farmName}</Typography>
          </Grid>

          <Grid item xs={12} md={6}>
            <Typography variant="subtitle2" color="text.secondary">
              Issue Date
            </Typography>
            <Typography variant="h6">{certificate.issueDate}</Typography>
          </Grid>

          <Grid item xs={12} md={6}>
            <Typography variant="subtitle2" color="text.secondary">
              Expiry Date
            </Typography>
            <Typography variant="h6">{certificate.expiryDate}</Typography>
          </Grid>

          <Grid item xs={12} md={6}>
            <Typography variant="subtitle2" color="text.secondary">
              QR Code
            </Typography>
            <Box mt={1}>
              {qrCodeUrl ? (
                <img src={qrCodeUrl} alt="Certificate QR Code" width={150} height={150} />
              ) : (
                <QrCode2 sx={{ fontSize: 60 }} />
              )}
            </Box>
          </Grid>
        </Grid>
      </Paper>
    </Container>
  );
}

'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import * as z from 'zod';
import {
  Container,
  Typography,
  Box,
  TextField,
  Button,
  Grid,
  Paper,
  MenuItem,
  Alert,
} from '@mui/material';
import { Save, ArrowBack } from '@mui/icons-material';
import apiClient from '@/lib/api-client';

// Schema validation
const certificateSchema = z.object({
  farmName: z.string().min(1, 'Farm Name is required'),
  farmerName: z.string().min(1, 'Farmer Name is required'),
  cropType: z.string().min(1, 'Crop Type is required'),
  issueDate: z.string().min(1, 'Issue Date is required'),
  expiryDate: z.string().min(1, 'Expiry Date is required'),
  status: z.enum(['Active', 'Expired', 'Revoked']).default('Active'),
});

type CertificateFormData = z.infer<typeof certificateSchema>;

export default function CreateCertificatePage() {
  const router = useRouter();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<CertificateFormData>({
    resolver: zodResolver(certificateSchema),
    defaultValues: {
      status: 'Active',
    },
  });

  const onSubmit = async (data: CertificateFormData) => {
    setIsSubmitting(true);
    setErrorMessage('');

    try {
      const payload = {
        farmName: data.farmName,
        farmerName: data.farmerName,
        cropType: data.cropType,
        validFrom: new Date(data.issueDate).toISOString(),
        validUntil: new Date(data.expiryDate).toISOString(),
        status: data.status,
        // Add other required fields if any, or let backend handle defaults
      };

      const response = await apiClient.post('/certificates', payload);

      if (response.data.success) {
        setSuccessMessage('Certificate created successfully!');
        // Redirect after short delay
        setTimeout(() => {
          router.push('/certificates');
        }, 1500);
      }
    } catch (err: any) {
      console.error('Error creating certificate:', err);
      setErrorMessage(err.response?.data?.message || 'Failed to create certificate. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Button
        startIcon={<ArrowBack />}
        onClick={() => router.back()}
        sx={{ mb: 2 }}
      >
        Back to List
      </Button>

      <Typography variant="h4" gutterBottom>
        ğŸ“ Issue New Certificate
      </Typography>

      {successMessage && (
        <Alert severity="success" sx={{ mb: 3 }}>
          {successMessage}
        </Alert>
      )}

      {errorMessage && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {errorMessage}
        </Alert>
      )}

      <Paper sx={{ p: 4 }}>
        <form onSubmit={handleSubmit(onSubmit)}>
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Farm Name"
                {...register('farmName')}
                error={!!errors.farmName}
                helperText={errors.farmName?.message}
              />
            </Grid>

            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Farmer Name"
                {...register('farmerName')}
                error={!!errors.farmerName}
                helperText={errors.farmerName?.message}
              />
            </Grid>

            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                select
                label="Crop Type"
                defaultValue=""
                inputProps={register('cropType')}
                error={!!errors.cropType}
                helperText={errors.cropType?.message}
              >
                <MenuItem value="Cannabis">Cannabis</MenuItem>
                <MenuItem value="Hemp">Hemp</MenuItem>
                <MenuItem value="Herbs">Herbs</MenuItem>
              </TextField>
            </Grid>

            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                type="date"
                label="Issue Date"
                InputLabelProps={{ shrink: true }}
                {...register('issueDate')}
                error={!!errors.issueDate}
                helperText={errors.issueDate?.message}
              />
            </Grid>

            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                type="date"
                label="Expiry Date"
                InputLabelProps={{ shrink: true }}
                {...register('expiryDate')}
                error={!!errors.expiryDate}
                helperText={errors.expiryDate?.message}
              />
            </Grid>

            <Grid item xs={12}>
              <Box display="flex" justifyContent="flex-end" gap={2}>
                <Button
                  variant="outlined"
                  onClick={() => router.back()}
                  disabled={isSubmitting}
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  variant="contained"
                  startIcon={<Save />}
                  disabled={isSubmitting}
                >
                  {isSubmitting ? 'Issuing...' : 'Issue Certificate'}
                </Button>
              </Box>
            </Grid>
          </Grid>
        </form>
      </Paper>
    </Container>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
  Box,
  Chip,
  TextField,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  CircularProgress,
  Alert,
} from '@mui/material';
import { Download, QrCode2, Visibility } from '@mui/icons-material';
import ErrorBoundary from '@/components/common/ErrorBoundary';
import apiClient from '@/lib/api-client';

interface Certificate {
  id: string;
  certificateNumber: string;
  farmName: string;
  status: string;
  issueDate: string;
  expiryDate: string;
}

export default function CertificatesPage() {
  const router = useRouter();
  const [certificates, setCertificates] = useState<Certificate[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('All');

  useEffect(() => {
    const fetchCertificates = async () => {
      try {
        const response = await apiClient.get('/certificates');
        if (response.data.success) {
          const mappedCerts = response.data.data.map((cert: any) => ({
            id: cert._id,
            certificateNumber: cert.certificateNumber,
            farmName: cert.farmName || 'Unknown Farm',
            status: cert.status,
            issueDate: new Date(cert.validFrom).toLocaleDateString('en-GB'),
            expiryDate: new Date(cert.validUntil).toLocaleDateString('en-GB'),
          }));
          setCertificates(mappedCerts);
        }
      } catch (err) {
        console.error('Failed to fetch certificates:', err);
        setError('Failed to load certificates. Please try again later.');
        // Fallback to mock data for demo purposes if backend is empty/fails
        setCertificates([
           { id: 'mock-1', certificateNumber: 'GACP-2025-0001', farmName: 'Mock Farm (Backend Offline)', status: 'Active', issueDate: '2025-01-10', expiryDate: '2026-01-10' }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchCertificates();
  }, []);

  const filteredCertificates = certificates.filter((cert) => {
    const matchesSearch =
      cert.certificateNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
      cert.farmName.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'All' || cert.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="50vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <ErrorBoundary>
      <Container maxWidth="xl" sx={{ py: 4 }}>
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
          <Typography variant="h4">
            ğŸ“œ My Certificates
          </Typography>
          <Button
            variant="contained"
            color="primary"
            onClick={() => router.push('/certificates/new')}
          >
            Issue Certificate
          </Button>
        </Box>

        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}

        <Box mb={4} display="flex" gap={2} flexWrap="wrap">
          <TextField
            label="Search Certificates"
            variant="outlined"
            size="small"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            sx={{ flexGrow: 1, minWidth: '200px' }}
          />
          <FormControl size="small" sx={{ minWidth: '150px' }}>
            <InputLabel>Status</InputLabel>
            <Select
              value={statusFilter}
              label="Status"
              onChange={(e) => setStatusFilter(e.target.value)}
            >
              <MenuItem value="All">All Status</MenuItem>
              <MenuItem value="Active">Active</MenuItem>
              <MenuItem value="Expired">Expired</MenuItem>
              <MenuItem value="Revoked">Revoked</MenuItem>
            </Select>
          </FormControl>
        </Box>

        <Grid container spacing={3}>
          {filteredCertificates.map((cert) => (
            <Grid item xs={12} md={6} lg={4} key={cert.id}>
              <Card>
                <CardContent>
                  <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                    <Typography variant="h6">{cert.certificateNumber}</Typography>
                    <Chip
                      label={cert.status}
                      color={
                        cert.status === 'Active'
                          ? 'success'
                          : cert.status === 'Expired'
                          ? 'error'
                          : 'default'
                      }
                      size="small"
                    />
                  </Box>
                  <Typography color="text.secondary" gutterBottom>
                    {cert.farmName}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Issued: {cert.issueDate}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" mb={2}>
                    Expires: {cert.expiryDate}
                  </Typography>
                  <Box display="flex" gap={1}>
                    <Button
                      size="small"
                      startIcon={<Visibility />}
                      variant="outlined"
                      onClick={() => router.push(`/certificates/${cert.id}`)}
                    >
                      View
                    </Button>
                    <Button size="small" startIcon={<Download />} variant="outlined">
                      PDF
                    </Button>
                    <Button size="small" startIcon={<QrCode2 />} variant="outlined">
                      QR
                    </Button>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          ))}
          {filteredCertificates.length === 0 && (
            <Grid item xs={12}>
              <Typography align="center" color="text.secondary">
                No certificates found.
              </Typography>
            </Grid>
          )}
        </Grid>
      </Container>
    </ErrorBoundary>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { Container, Typography, Grid, Card, CardContent, Box } from '@mui/material';
import { CheckCircle, Pending, Cancel, Description } from '@mui/icons-material';
import ErrorBoundary from '@/components/common/ErrorBoundary';

export default function DashboardPage() {
  const router = useRouter();
  const [stats, setStats] = useState({
    active: 12,
    pending: 3,
    expired: 2,
    total: 17,
  });

  useEffect(() => {
    const token = localStorage.getItem('cert_token');
    if (!token) {
      router.push('/login');
    }
  }, [router]);

  const cards = [
    { title: 'Active Certificates', value: stats.active, icon: <CheckCircle sx={{ fontSize: 48, color: '#4caf50' }} />, color: '#4caf50' },
    { title: 'Pending', value: stats.pending, icon: <Pending sx={{ fontSize: 48, color: '#ff9800' }} />, color: '#ff9800' },
    { title: 'Expired', value: stats.expired, icon: <Cancel sx={{ fontSize: 48, color: '#f44336' }} />, color: '#f44336' },
    { title: 'Total', value: stats.total, icon: <Description sx={{ fontSize: 48, color: '#2196f3' }} />, color: '#2196f3' },
  ];

  return (
    <ErrorBoundary>
      <Container maxWidth="xl" sx={{ py: 4 }}>
        <Typography variant="h4" gutterBottom>
          ğŸ“Š Dashboard
        </Typography>
        <Grid container spacing={3}>
          {cards.map((card, index) => (
            <Grid item xs={12} sm={6} md={3} key={index}>
              <Card>
                <CardContent>
                  <Box display="flex" flexDirection="column" alignItems="center" textAlign="center">
                    {card.icon}
                    <Typography variant="h4" sx={{ mt: 2, color: card.color }}>
                      {card.value}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {card.title}
                    </Typography>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    </ErrorBoundary>
  );
}

import type { Metadata } from 'next';
import { Inter } from 'next/font/google';
import './globals.css';

const inter = Inter({ subsets: ['latin'] });

export const metadata: Metadata = {
  title: 'GACP Certificate Portal',
  description: 'Certificate Issuance and Management System for GACP Platform',
  keywords: ['GACP', 'Certificate', 'Agriculture', 'Certification']
};

export default function RootLayout({ children }: { children: React.ReactNode }) {
  return (
    <html lang="th">
      <body className={inter.className}>{children}</body>
    </html>
  );
}

import React from 'react';
import { render, screen, waitFor, fireEvent } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import { useRouter } from 'next/navigation';
import { useSnackbar } from 'notistack';
import LoginPage from '../page';

// Mock next/navigation
const mockPush = jest.fn();
jest.mock('next/navigation', () => ({
  useRouter: jest.fn()
}));

// Mock notistack
const mockEnqueueSnackbar = jest.fn();
jest.mock('notistack', () => ({
  useSnackbar: jest.fn()
}));

// Mock localStorage
const mockLocalStorage = (() => {
  let store: Record<string, string> = {};
  return {
    getItem: jest.fn((key: string) => store[key] || null),
    setItem: jest.fn((key: string, value: string) => {
      store[key] = value;
    }),
    clear: jest.fn(() => {
      store = {};
    }),
    store
  };
})();

Object.defineProperty(window, 'localStorage', {
  value: mockLocalStorage
});

describe('LoginPage', () => {
  beforeEach(() => {
    jest.useFakeTimers();
    (useRouter as jest.Mock).mockReturnValue({ push: mockPush });
    (useSnackbar as jest.Mock).mockReturnValue({ enqueueSnackbar: mockEnqueueSnackbar });
    mockLocalStorage.clear();
    mockPush.mockClear();
    mockEnqueueSnackbar.mockClear();
  });

  afterEach(() => {
    jest.useRealTimers();
    jest.clearAllMocks();
  });

  describe('Page Rendering', () => {
    it('should render login form', () => {
      render(<LoginPage />);
      expect(screen.getByLabelText(/à¸­à¸µà¹€à¸¡à¸¥/i)).toBeInTheDocument();
      expect(screen.getByLabelText(/à¸£à¸«à¸±à¸ªà¸œà¹ˆà¸²à¸™/i)).toBeInTheDocument();
    });

    it('should render login button', () => {
      render(<LoginPage />);
      expect(screen.getByRole('button', { name: /à¹€à¸‚à¹‰à¸²à¸ªà¸¹à¹ˆà¸£à¸°à¸šà¸š/i })).toBeInTheDocument();
    });

    it('should render page title', () => {
      render(<LoginPage />);
      expect(screen.getByRole('heading', { name: /Certificate Portal/i })).toBeInTheDocument();
    });

    it('should have password visibility toggle', () => {
      render(<LoginPage />);
      const passwordInput = screen.getByLabelText(/à¸£à¸«à¸±à¸ªà¸œà¹ˆà¸²à¸™/i);
      expect(passwordInput).toHaveAttribute('type', 'password');
    });
  });

  describe('Form Interaction', () => {
    it('should allow typing in email field', async () => {
      const user = userEvent.setup({ delay: null });
      render(<LoginPage />);
      const emailInput = screen.getByLabelText(/à¸­à¸µà¹€à¸¡à¸¥/i);
      await user.type(emailInput, 'test@example.com');
      expect(emailInput).toHaveValue('test@example.com');
    });

    it('should allow typing in password field', async () => {
      const user = userEvent.setup({ delay: null });
      render(<LoginPage />);
      const passwordInput = screen.getByLabelText(/à¸£à¸«à¸±à¸ªà¸œà¹ˆà¸²à¸™/i);
      await user.type(passwordInput, 'password123');
      expect(passwordInput).toHaveValue('password123');
    });

    it('should toggle password visibility', async () => {
      const user = userEvent.setup({ delay: null });
      render(<LoginPage />);
      const passwordInput = screen.getByLabelText(/à¸£à¸«à¸±à¸ªà¸œà¹ˆà¸²à¸™/i);
      const toggleButtons = screen.getAllByRole('button');
      const visibilityToggle = toggleButtons.find(
        btn =>
          btn.querySelector('[data-testid="VisibilityOffIcon"]') ||
          btn.querySelector('[data-testid="VisibilityIcon"]')
      );

      expect(passwordInput).toHaveAttribute('type', 'password');

      if (visibilityToggle) {
        await user.click(visibilityToggle);
        expect(passwordInput).toHaveAttribute('type', 'text');
      }
    });
  });

  describe('Login Success', () => {
    it('should login successfully with correct credentials', async () => {
      const user = userEvent.setup({ delay: null });
      render(<LoginPage />);

      const emailInput = screen.getByLabelText(/à¸­à¸µà¹€à¸¡à¸¥/i);
      const passwordInput = screen.getByLabelText(/à¸£à¸«à¸±à¸ªà¸œà¹ˆà¸²à¸™/i);
      const loginButton = screen.getByRole('button', { name: /à¹€à¸‚à¹‰à¸²à¸ªà¸¹à¹ˆà¸£à¸°à¸šà¸š/i });

      await user.type(emailInput, 'cert@gacp.test');
      await user.type(passwordInput, 'password123');
      fireEvent.click(loginButton);

      jest.advanceTimersByTime(1000);

      await waitFor(() => {
        expect(mockLocalStorage.setItem).toHaveBeenCalledWith(
          'cert_token',
          'demo-token-certificate-officer'
        );
      });

      await waitFor(() => {
        expect(mockEnqueueSnackbar).toHaveBeenCalledWith('à¹€à¸‚à¹‰à¸²à¸ªà¸¹à¹ˆà¸£à¸°à¸šà¸šà¸ªà¸³à¹€à¸£à¹‡à¸ˆ!', {
          variant: 'success'
        });
      });

      await waitFor(() => {
        expect(mockPush).toHaveBeenCalledWith('/dashboard');
      });
    });

    it('should store user data in localStorage', async () => {
      const user = userEvent.setup({ delay: null });
      render(<LoginPage />);

      await user.type(screen.getByLabelText(/à¸­à¸µà¹€à¸¡à¸¥/i), 'cert@gacp.test');
      await user.type(screen.getByLabelText(/à¸£à¸«à¸±à¸ªà¸œà¹ˆà¸²à¸™/i), 'password123');
      fireEvent.click(screen.getByRole('button', { name: /à¹€à¸‚à¹‰à¸²à¸ªà¸¹à¹ˆà¸£à¸°à¸šà¸š/i }));

      jest.advanceTimersByTime(1000);

      await waitFor(() => {
        expect(mockLocalStorage.setItem).toHaveBeenCalledWith(
          'cert_user',
          expect.stringContaining('certificate_officer')
        );
      });
    });
  });

  describe('Login Failure', () => {
    it('should show error with incorrect credentials', async () => {
