/**
 * Format date to Thai locale
 */
export function formatDateThai(date: string | Date): string {
  const d = typeof date === 'string' ? new Date(date) : date;
  return d.toLocaleDateString('th-TH', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });
}

/**
 * Format date to short Thai format
 */
export function formatDateShortThai(date: string | Date): string {
  const d = typeof date === 'string' ? new Date(date) : date;
  return d.toLocaleDateString('th-TH', {
    year: '2-digit',
    month: '2-digit',
    day: '2-digit'
  });
}

/**
 * Format number with Thai locale
 */
export function formatNumber(num: number, decimals: number = 2): string {
  return num.toLocaleString('th-TH', {
    minimumFractionDigits: decimals,
    maximumFractionDigits: decimals
  });
}

/**
 * Calculate days until expiry
 */
export function getDaysUntilExpiry(expiryDate: string | Date): number {
  const expiry = typeof expiryDate === 'string' ? new Date(expiryDate) : expiryDate;
  const today = new Date();
  const diffTime = expiry.getTime() - today.getTime();
  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  return diffDays;
}

/**
 * Check if certificate is expiring soon (within 30 days)
 */
export function isExpiringSoon(expiryDate: string | Date): boolean {
  const daysUntilExpiry = getDaysUntilExpiry(expiryDate);
  return daysUntilExpiry > 0 && daysUntilExpiry <= 30;
}

/**
 * Check if certificate is expired
 */
export function isExpired(expiryDate: string | Date): boolean {
  const daysUntilExpiry = getDaysUntilExpiry(expiryDate);
  return daysUntilExpiry < 0;
}

/**
 * Validate Thai National ID
 */
export function validateNationalId(id: string): boolean {
  if (!/^\d{13}$/.test(id)) return false;

  let sum = 0;
  for (let i = 0; i < 12; i++) {
    sum += parseInt(id.charAt(i)) * (13 - i);
  }
  const mod = sum % 11;
  const check = (11 - mod) % 10;
  return check === parseInt(id.charAt(12));
}

/**
 * Validate Thai postal code
 */
export function validatePostalCode(postalCode: string): boolean {
  return /^\d{5}$/.test(postalCode);
}

/**
 * Truncate text with ellipsis
 */
export function truncateText(text: string, maxLength: number): string {
  if (text.length <= maxLength) return text;
  return text.substring(0, maxLength) + '...';
}

/**
 * Generate certificate number
 */
export function generateCertificateNumber(
  standard: string,
  year: number,
  sequence: number
): string {
  const paddedSequence = sequence.toString().padStart(4, '0');
  return `${standard}-${year}-${paddedSequence}`;
}

/**
 * Parse certificate number
 */
export function parseCertificateNumber(certificateNumber: string): {
  standard: string;
  year: number;
  sequence: number;
} | null {
  const match = certificateNumber.match(/^([A-Z]+)-(\d{4})-(\d{4})$/);
  if (!match) return null;

  return {
    standard: match[1],
    year: parseInt(match[2]),
    sequence: parseInt(match[3])
  };
}

/**
 * Get certificate status color
 */
export function getStatusColor(status: string): string {
  switch (status) {
    case 'approved':
      return '#4caf50';
    case 'pending':
      return '#ff9800';
    case 'rejected':
      return '#f44336';
    case 'expired':
      return '#9e9e9e';
    case 'revoked':
      return '#f44336';
    default:
      return '#2196f3';
  }
}

/**
 * Get certificate status label in Thai
 */
export function getStatusLabelThai(status: string): string {
  switch (status) {
    case 'approved':
      return 'à¸­à¸™à¸¸à¸¡à¸±à¸•à¸´à¹à¸¥à¹‰à¸§';
    case 'pending':
      return 'à¸£à¸­à¸­à¸™à¸¸à¸¡à¸±à¸•à¸´';
    case 'rejected':
      return 'à¸›à¸à¸´à¹€à¸ªà¸˜';
    case 'expired':
      return 'à¸«à¸¡à¸”à¸­à¸²à¸¢à¸¸';
    case 'revoked':
      return 'à¸¢à¸à¹€à¸¥à¸´à¸';
    default:
      return status;
  }
}

/**
 * Debounce function
 */
export function debounce<T extends (...args: any[]) => any>(
  func: T,
  wait: number
): (...args: Parameters<T>) => void {
  let timeout: NodeJS.Timeout | null = null;

  return function executedFunction(...args: Parameters<T>) {
    const later = () => {
      timeout = null;
      func(...args);
    };

    if (timeout) {
      clearTimeout(timeout);
    }
    timeout = setTimeout(later, wait);
  };
}

/**
 * Deep clone object
 */
export function deepClone<T>(obj: T): T {
  return JSON.parse(JSON.stringify(obj));
}

/**
 * Check if object is empty
 */
export function isEmpty(obj: any): boolean {
  if (obj === null || obj === undefined) return true;
  if (typeof obj === 'string') return obj.trim().length === 0;
  if (Array.isArray(obj)) return obj.length === 0;
  if (typeof obj === 'object') return Object.keys(obj).length === 0;
  return false;
}

export default {
  formatDateThai,
  formatDateShortThai,
  formatNumber,
  getDaysUntilExpiry,
  isExpiringSoon,
  isExpired,
  validateNationalId,
  validatePostalCode,
  truncateText,
  generateCertificateNumber,
  parseCertificateNumber,
  getStatusColor,
  getStatusLabelThai,
  debounce,
  deepClone,
  isEmpty
};

import jsPDF from 'jspdf';
import QRCode from 'qrcode';

export const generateCertificatePDF = async (certificate: any) => {
  const doc = new jsPDF({
    orientation: 'landscape',
    unit: 'mm',
    format: 'a4',
  });

  // Background
  doc.setFillColor(255, 255, 255);
  doc.rect(0, 0, 297, 210, 'F');

  // Border
  doc.setLineWidth(2);
  doc.setDrawColor(46, 125, 50); // Green color
  doc.rect(10, 10, 277, 190);

  // Title
  doc.setFontSize(30);
  doc.setTextColor(46, 125, 50);
  doc.text('GACP Certificate', 148.5, 40, { align: 'center' });

  // Certificate ID
  doc.setFontSize(12);
  doc.setTextColor(100, 100, 100);
  doc.text(`Certificate ID: ${certificate.id}`, 148.5, 50, { align: 'center' });

  // Content
  doc.setFontSize(16);
  doc.setTextColor(0, 0, 0);

  const startY = 80;
  const lineHeight = 15;

  doc.text(`This is to certify that`, 148.5, startY, { align: 'center' });

  doc.setFontSize(24);
  doc.setFont('helvetica', 'bold');
  doc.text(certificate.farmName, 148.5, startY + lineHeight, { align: 'center' });

  doc.setFontSize(16);
  doc.setFont('helvetica', 'normal');
  doc.text(`has successfully met the requirements for`, 148.5, startY + lineHeight * 2.5, { align: 'center' });

  doc.setFontSize(20);
  doc.text(`Good Agricultural and Collection Practices`, 148.5, startY + lineHeight * 3.5, { align: 'center' });

  // Details
  doc.setFontSize(12);
  doc.text(`Issue Date: ${certificate.issueDate}`, 50, 160);
  doc.text(`Expiry Date: ${certificate.expiryDate}`, 50, 170);
  doc.text(`Status: ${certificate.status}`, 50, 180);

  // QR Code
  try {
    const qrDataUrl = await QRCode.toDataURL(JSON.stringify(certificate));
    doc.addImage(qrDataUrl, 'PNG', 220, 140, 40, 40);
  } catch (err) {
    console.error('Error generating QR for PDF', err);
  }

  // Footer
  doc.setFontSize(10);
  doc.setTextColor(150, 150, 150);
  doc.text('This certificate is electronically generated and valid without signature.', 148.5, 195, { align: 'center' });

  // Save
  doc.save(`${certificate.id}.pdf`);
};

import QRCode from 'qrcode';

interface QRCodeOptions {
  width?: number;
  margin?: number;
  color?: {
    dark?: string;
    light?: string;
  };
  errorCorrectionLevel?: 'L' | 'M' | 'Q' | 'H';
}

/**
 * Generate QR code as data URL
 */
export async function generateQRCode(data: string, options?: QRCodeOptions): Promise<string> {
  const defaultOptions: QRCodeOptions = {
    width: 200,
    margin: 2,
    color: {
      dark: '#000000',
      light: '#FFFFFF'
    },
    errorCorrectionLevel: 'M',
    ...options
  };

  try {
    const qrCodeDataURL = await QRCode.toDataURL(data, defaultOptions);
    return qrCodeDataURL;
  } catch (error) {
    console.error('Error generating QR code:', error);
    throw new Error('Failed to generate QR code');
  }
}

/**
 * Generate QR code as canvas
 */
export async function generateQRCodeCanvas(
  data: string,
  canvas: HTMLCanvasElement,
  options?: QRCodeOptions
): Promise<void> {
  const defaultOptions: QRCodeOptions = {
    width: 200,
    margin: 2,
    errorCorrectionLevel: 'M',
    ...options
  };

  try {
    await QRCode.toCanvas(canvas, data, defaultOptions);
  } catch (error) {
    console.error('Error generating QR code canvas:', error);
    throw new Error('Failed to generate QR code canvas');
  }
}

/**
 * Generate certificate verification QR code
 * Contains certificate number and verification URL
 */
export async function generateCertificateQR(
  certificateNumber: string,
  options?: QRCodeOptions
): Promise<string> {
  const verificationURL = `${process.env.NEXT_PUBLIC_API_URL}/verify/${certificateNumber}`;
  return generateQRCode(verificationURL, options);
}

/**
 * Download QR code as image
 */
export function downloadQRCode(dataURL: string, filename: string = 'qrcode.png'): void {
  const link = document.createElement('a');
  link.href = dataURL;
  link.download = filename;
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
}

/**
 * Validate QR code data
 */
export function isValidQRData(data: string): boolean {
  if (!data || typeof data !== 'string') {
    return false;
  }

  // Check if data is not too long (QR code has limits)
  if (data.length > 4296) {
    return false;
  }

  return true;
}

